module acrm_paras
    use ACRM_DataSpec, only : refr, phis
    implicit none
    save
    integer,parameter::ncomp0=10, nspchnl=2001, nknots=9, nangls=18, ncub=48
    real(8),parameter::dr=acos(-1.d0)/180, pi=acos(-1.d0), wlstep=1.d0
    real(8) :: acoef1(nspchnl,10),acoef2(nspchnl,10),thknot(nangls),p14(nknots)

end module acrm_paras
!***********************************************************************

subroutine LoadACRMdata
    use ACRM_DataSpec
    use acrm_paras,only:nspchnl,acoef1,acoef2,ncub,thknot,p14,pi,nknots

    real(8) :: zctst(ncub),acztst(ncub),x1,x2
    integer :: j,ntstz

    ntstz=nknots-1
    x1=0.0
    x2=pi/2.0
    !call acrm_gauleg(x1,x2,zctst,acztst,ntstz)
    call gauss(x1,x2,zctst,acztst,ntstz)
    thknot(1)=0.d0
    p14(1)=0.d0

    do j=2,nknots
      thknot(j)=zctst(j-1)
      p14(j)=cos(thknot(j))*acztst(j-1)
    enddo

    do jw = 1, nspchnl
        acoef2(jw,1) = waterb(jw)
        acoef2(jw,2) = chlorp3(jw)
        acoef2(jw,3) = drymatter(jw)
        acoef2(jw,4) = anthocyanins(jw)
    enddo

    do jw = 1, nspchnl
        acoef1(jw,1) = waterb(jw)
        acoef1(jw,2) = chlorp3(jw)
        acoef1(jw,3) = anthocyanins(jw)
        acoef1(jw,4) = drymatter(jw)
        acoef1(jw,5) = cellp3(jw)
    enddo

end subroutine LoadACRMdata

!***********************************************************************
subroutine ACRM(LAI2,sl2_0,clmp2,szz,eln2,thm2_0,nratio2,slw2,lmod2,&
                ncomp2,ccomp2,N2,dcell2,asp2, &
                LAI1,sl1_0,clmp1,eln1,thm1_0,nratio1,slw1,lmod1, &
                ncomp1,ccomp1,N1,dcell1,asp1,&
                s1,s2,s3,s4,&
                thets,thetv,phi,rlambda,y)
    use acrm_paras,only:ncomp0
    implicit none
    real(8),intent(in) :: thets,thetv,phi,rlambda ! thets,thetv,phi in radian
    real(8),intent(out) :: y
    character(30)::lmod1,lmod2
    integer :: ncomp2,ncomp1
    real(8),intent(in)::LAI2,sl2_0,clmp2,szz,eln2,thm2_0,nratio2,slw2,N2,dcell2,asp2, &
         LAI1,sl1_0,clmp1,eln1,thm1_0,nratio1,slw1,N1,dcell1,asp1, &
         s1,s2,s3,s4,ccomp1(ncomp0),ccomp2(ncomp0)
    real(8) :: ul2,ul1,ee2,ee1,vai2,vai1,rnc2,rnc1,rsl1,rsl2,rsl3,rsl4,dcell0
    real(8) :: thssr,thss,sl2,sl1,thm2,thm1
    real(8) :: pkhair
    real(8) :: bc1,bs1,bdplants,bdsoil
    real(8) :: pi,dr

    pi = acos(-1.d0)
    dr = pi / 180.0

    !thets = thets0 * dr
    !thetv = thetv0 * dr
    !phi = phi0 * dr

    dcell0=80.0

    thssr=45.0
    pkhair=0.1

    ul2=LAI2*clmp2
    sl2 = sl2_0
    if(sl2_0 .le. 0.d0) sl2=.010
    ee2=1.d0-exp(-eln2)
    thm2=thm2_0*dr
    rnc2=nratio2
    vai2=N2/clmp2

    ul1=LAI1*clmp1
    sl1 = sl1_0
    if(sl1_0 .le. 0.d0) sl1=.010
    ee1=1.0-exp(-eln1)
    thm1=thm1_0*dr
    rnc1=nratio1
    vai1=N1/clmp1

    rsl1=s1
    rsl2=s2
    rsl3=s3
    rsl4=s4
    thss=thssr*dr

    call smcrm(lmod2, ncomp2, ul2, sl2, szz, eln2, thm2, &
      slw2, rnc2, vai2, dcell2, asp2, ccomp2, &
      lmod1, ncomp1, ul1, sl1, eln1, thm1, &
      slw1, rnc1, vai1, dcell1, asp1, ccomp1, &
      thets, pkhair, dcell0, rsl1, rsl2, rsl3, rsl4, thss, &
      thetv, phi, rlambda, ee1, ee2, &
      bc1, bs1, bdplants, bdsoil)

    y  = (bc1 + bs1) + bdplants + bdsoil

end subroutine ACRM

!*********************************************************************
subroutine smcrm(lmod2, ncomp2, ul2, sl2, szz, eln2, thm2, &
        slw2, rnc2, vai2, dcell2, asp2, ccomp2, &
        lmod1, ncomp1, ul1, sl1, eln1, thm1, &
        slw1, rnc1, vai1, dcell1, asp1, ccomp1, &
        thets, pkhair, dcell0, rsl1, rsl2, rsl3, rsl4, thss, &
        thetv, phi, rlambda, ee1, ee2, &
        bc1, bs1, bdplants, bdsoil)

    use acrm_paras,only:phis,refr,acoef2,acoef1,wlstep,ncomp0
    implicit none

    real(8),intent(in)::ul2, sl2, szz, eln2, thm2, &
        slw2, rnc2, vai2, ccomp2, dcell2, asp2, &
        ul1, sl1, eln1, thm1, &
        slw1, rnc1, vai1, ccomp1, dcell1, asp1, &
        thets, pkhair, dcell0, rsl1, rsl2, rsl3, rsl4, thss, &
        thetv, phi, rlambda, ee1, ee2
    real(8),intent(out)::bc1, bs1, bdplants, bdsoil
    character(30),intent(in) :: lmod2, lmod1
    integer,intent(in) :: ncomp2, ncomp1

    integer ::jw,jc
    dimension ccomp1(ncomp0),ccomp2(ncomp0)
    real(8) :: eps1,eps2,rsl,rnnl,dkl2,refl2,tran2,dkl1,refl1,tran1, &
        ellmy1,ellsi1,ellmy2,ellsi2,rn1,rrx,rrl1,ttl1,rn2, &
        rrl2,ttl2,sz1,gg1s,gg1v,gamm1,gg2s,gg2v,gamm2,ggv, &
        coeff1,coeff2,rinf

    data eps1/.0175d0/,eps2/.1d-5/

    jw = int((rlambda - (400.d0 - wlstep))/wlstep)
    rsl = rsl1*phis(jw, 1) + rsl2*phis(jw, 2) + &
        rsl3*phis(jw, 3) + rsl4*phis(jw, 4)

    if (rsl .lt. 0.d0) rsl = 0.d0

    if ((ul1 + ul2) .gt. eps2) then
        rnnl = refr(jw)
        dkl2 = 0.d0
        do jc = 1, ncomp2
            dkl2 = dkl2 + ccomp2(jc)*acoef2(jw, jc)
        enddo

        if (lmod2 .eq. 'prospect') then
            dkl2 = dkl2/vai2*slw2*1.d-6
            call prospect(rnnl, dkl2, vai2, refl2, tran2)
        else if (lmod2 .eq. 'liberty') then
            coeff2 = dkl2*dcell2/dcell0*slw2*1.d-6/vai2
            if (coeff2 .lt. eps1) then
                !penalty = eps1 - coeff2
                coeff2 = eps1
            endif
            call liberty(rnnl,coeff2,vai2,asp2,rinf,refl2,tran2)
        endif

        dkl1 = 0.d0
        do jc = 1, ncomp1
            dkl1 = dkl1 + ccomp1(jc)*acoef1(jw, jc)
        enddo

        if (lmod1 .eq. 'prospect') then
            dkl1 = dkl1/vai1*slw1*1.d-6
            call prospect(rnnl, dkl1, vai1, refl1, tran1)
        else if (lmod1 .eq. 'liberty') then
            coeff1 = dkl1*dcell1/dcell0*slw1*1.d-6/vai1
            if (coeff1 .lt. eps1) then
                !penalty = eps1 - coeff1
                coeff1 = eps1
            endif
            call liberty(rnnl,coeff1,vai1,asp1,rinf,refl1,tran1)
        endif

        ellmy1  = abs(.059d0*eln1*(thm1 - 1.02d0) + .02d0)
        ellsi1  = abs(.0107d0 - .0216d0*eln1*(thm1 - .265d0))
        ellmy2  = abs(.059d0*eln2*(thm2 - 1.02d0) + .02d0)
        ellsi2  = abs(.0107d0 - .0216d0*eln2*(thm2 - .265d0))

        rn1   = max(rnc1*rnnl, 1.d0)
        rrx   = refl1 - ((1.d0 - rn1)/(1.d0 + rn1))**2
        rrl1  = max(rrx, 0.d0)
        ttl1  = tran1
        rn2   = max(rnc2*rnnl, 1.d0)
        rrx   = refl2 - ((1.d0 - rn2)/(1.d0 + rn2))**2
        rrl2  = max(rrx, 0.d0)
        ttl2  = tran2

        sz1   = 1.d0

        call gamma(thetv, thets, phi, thm1, ee1, sz1, rrl1, ttl1, rn1, &
            pkhair, ellmy1, ellsi1, gg1s, gg1v, gamm1)
        call gamma(thetv, thets, phi, thm2, ee2, szz, rrl2, ttl2, rn2, &
            pkhair, ellmy2, ellsi2, gg2s, gg2v, gamm2)
    endif
    ggv = gg2v

    call biz2(thetv, thets, phi, ul2, sl2, gg2s, gg2v, gamm2, &
        ul1, sl1, gg1s, gg1v, gamm1, thss, rsl, bc1, bs1)

    rrl1 = refl1
    rrl2 = refl2

    call dif2(thetv, thets, &
        gg1s, gg1v, ul1, rrl1, ttl1, ellmy1, ellsi1, thm1, &
        gg2s, gg2v, ul2, rrl2, ttl2, ellmy2, ellsi2, thm2, &
        rsl, thss, bdplants, bdsoil)

    return
end subroutine smcrm

!*****************************************************************
subroutine biz2(th2, th1, phi, ul2, sl2, gg2s, gg2v, gamm2, &
     ul1, sl1, gg1s, gg1v, gamm1, thss, rsl, bc1, bs1)

    implicit none

    real(8),intent(in)::th2, th1, phi, ul2, sl2, gg2s, gg2v, gamm2, &
             ul1, sl1, gg1s, gg1v, gamm1, thss, rsl
    real(8),intent(out) :: bc1, bs1
    real(8) :: eps,eps2,rsosoil,rsdsoil,rodsoil,rddsoil, &
             sth2,cth2,sth1,cth1,cp,calph,catmp,alph,deltx,delta, &
             xyc1,xyc2,chsh1,chsh2,qq1,qq2,rc21,rc11
    real(8),external::eint

    data eps/.1d-4/, eps2/.1d-5/

    call acrm_soil(rsl,th2,th1,thss,phi,rsosoil,rsdsoil,rodsoil,rddsoil)

    if ((ul1 + ul2) .gt. eps) goto 2
    bc1 = 0.d0
    bs1 = rsosoil
    goto 1

2   continue
    sth2  = sin(th2)
    cth2  = cos(th2)
    sth1  = sin(th1)
    cth1  = cos(th1)
    cp    = cos(phi)

    calph = sth2*sth1*cp + cth2*cth1
    catmp = calph
    ! alph  = acos(catmp)
    if(catmp.gt.1.0) catmp=1.0
    if(catmp.lt.-1.0) catmp=-1.0
    alph  = acos(catmp)
    !

    deltx = 1.d0/(cth1**2) + 1.d0/(cth2**2) - 2.d0*calph/cth1/cth2
    if (abs(deltx) .lt. eps2) then
       delta = 0.d0
    else
       delta = sqrt(1.d0/(cth1**2) + 1.d0/(cth2**2) - 2.d0*calph/cth1/cth2)
    endif

    xyc1  = delta/sl1
    chsh1 = exp(sqrt(gg1s/cth1*gg1v/cth2)*ul1*eint(xyc1))
    qq1   = exp(-ul1*(gg1s/cth1 + gg1v/cth2))*chsh1
    xyc2  = delta/sl2
    chsh2 = exp(sqrt(gg2s/cth1*gg2v/cth2)*ul2*eint(xyc2))
    qq2   = exp(-ul2*(gg2s/cth1 + gg2v/cth2))*chsh2

    bs1   = rsosoil*qq1*qq2

    if (ul2 .le. 0.d0) then
       rc21 = 0.d0
    else

    call rhoc1(cth2, cth1, alph, ul2, sl2, gg2s, gg2v, gamm2, rc21)
    endif
    if (ul1 .le. 0.d0) then
       rc11 = 0.d0
    else

    call rhoc1(cth2, cth1, alph, ul1, sl1, gg1s, gg1v, gamm1, rc11)
    endif

    bc1  = rc21 + rc11*qq2
1   continue

    return
end subroutine biz2
!******************************************************************
function eint(xy)
    implicit none
    real(8),intent(in) :: xy
    real(8) :: eps,eint
    data eps/.1d-6/

    if (abs(xy) .lt. eps) then
       eint = 1.d0 - xy*(.5d0 - xy/6.d0*(1.d0 - xy*.25d0))
    else
      ! eint = (1.d0 - exp(-xy))/xy
      if((-xy)<-87) then
          eint = 1.0 / xy
      else
          eint = (1.d0 - exp(-xy))/xy
      endif
    endif
    return
end function eint

!*****************************************************************
subroutine acrm_soil(rsl, th2, th1, thss, phi, rsosoil, rsdsoil, rodsoil, rddsoil)
    implicit none
    real(8),intent(in) :: rsl,th2,th1,thss,phi
    real(8),intent(out) :: rsosoil,rsdsoil,rodsoil,rddsoil
    real(8) :: asl,bsl,csl,cts,ths1,thsss,cp,rslcts
    save asl, bsl, csl, cts, ths1, thsss

    data asl/.45098d0/, bsl/5.7829d0/, csl, cts/2*13.7575d0/
    data ths1, thsss/2*.785398163d0/

    if (thss .ne. thsss) then
       cts   = 16.41d0 - thss*thss*4.3d0
       thsss = thss
    endif

    if (th1 .ne. ths1) then
       ths1 = th1
       asl  = th1*th1*7.702d0 - 4.3d0
       bsl  = th1*7.363d0
       csl  = 16.41d0 - th1*th1*4.3d0
    endif

    cp      = cos(phi)
    rslcts  = rsl/cts
    rsosoil = ((asl*th2 + bsl*cp)*th2 + csl)*rslcts
    rsdsoil = (th1*th1*5.651d0 + 10.1d0)*rslcts
    rodsoil = (th2*th2*5.651d0 + 10.1d0)*rslcts
    rddsoil = 14.25d0*rslcts
    return
end subroutine acrm_soil
!*****************************************************************
subroutine rhoc1(cth2, cth1, alph, ul, sl, gg1, gg2, gamm, rc1)
    implicit none
    real(8),intent(in)::cth2,cth1,alph,ul,sl,gg1,gg2,gamm
    real(8),intent(out) :: rc1
    real(8) :: xy1,ss1,xy2,ss2,xy3,ss3
    real(8),external::eint

    xy1  = ul*(gg1/cth1 + gg2/cth2)
    ss1  = ul/cth1/cth2*eint(xy1)
    xy2  = ul/2.d0*(gg1/cth1 + gg2/cth2 + alph/(sl*ul*sqrt(cth1*cth2)))
    ss2  = ul/cth1/cth2*eint(xy2)
    xy3  = ul*(gg1/cth1 + gg2/cth2 + alph/(2.d0*sl*ul*sqrt(cth1*cth2)))
    ss3  = ul/cth1/cth2*eint(xy3)
    rc1  = gamm*(ss1 + ss2 - ss3)
    return
 end subroutine rhoc1
!*****************************************************************
subroutine prospect(rnnl, dkl_0, vai, refl, tran)
    implicit none
    real(8),intent(in)::rnnl,dkl_0,vai
    real(8),intent(out)::refl,tran
    real(8) :: rinex,teta,refra,tau,t1,t2, &
            x1,x2,x3,x4,x5,x6,rlf,tlf,ra,ta,deltp,alfp,betp,va,vb, &
            sp1,sp2,sp3,dr,dkl
    dr = acos(-1.d0) / 180.0

    dkl = dkl_0

    if (dkl .le. 0.d0) then
      dkl = 1.d0
    else
      call acrm_s13aaf(dkl, rinex)
      dkl = (1.d0 - dkl)*exp(-dkl) + dkl**2*rinex
    endif

    teta  = 90.d0*dr
    refra = rnnl

    call acrm_tav(teta, refra, tau)

    t1    = tau
    teta  = 60.d0*dr

    call acrm_tav(teta, refra, tau)

    t2    = tau
    x1    = 1.d0 - t1
    x2    = t1**2*dkl**2*(rnnl**2 - t1)
    x3    = t1**2*dkl*rnnl**2
    x4    = rnnl**4 - dkl**2*(rnnl**2 - t1)**2
    x5    = t2/t1
    x6    = x5*(t1 - 1.d0) + 1.d0 - t2
    rlf   = x1 + x2/x4
    tlf   = x3/x4
    ra    = x5*rlf + x6
    ta    = x5*tlf

    deltp = (tlf**2 - rlf**2 - 1.d0)**2 - 4.d0*rlf**2
    alfp  = (1.d0 + rlf**2 - tlf**2 + dsqrt(deltp))/(2.d0*rlf)
    betp  = (1.d0 + rlf**2 - tlf**2 - dsqrt(deltp))/(2.d0*rlf)
    va    = (1.d0 + rlf**2 - tlf**2 + dsqrt(deltp))/(2.d0*rlf)
    vb    = dsqrt(betp*(alfp - rlf)/(alfp*(betp - rlf)))
    sp1   = ra*(va*vb**(vai - 1.d0) - va**(-1)*vb**(-(vai - 1.d0))) &
          + (ta*tlf - ra*rlf)*(vb**(vai - 1.d0) - vb**(-(vai - 1.d0)))
    sp2   = ta*(va - va**(-1.d0))
    sp3   = va*vb**(vai - 1.d0) - va**(-1)*vb**(-(vai - 1.d0)) &
          - rlf*(vb**(vai - 1.d0) - vb**(-(vai - 1.d0)))
    refl  = sp1/sp3
    tran  = sp2/sp3
    return
end subroutine prospect

!*********************************************************************
subroutine acrm_s13aaf(dkl, rinex)
    implicit none
    real(8),intent(in)::dkl
    real(8),intent(out)::rinex
    real(8) :: xs,ys

    if (dkl .gt. 4.d0) goto 10

    xs = 0.5d0 * dkl - 1.d0
    ys = ((((((((((((((( - 3.60311230482612224d-13 &
      *xs + 3.46348526554087424d-12)*xs - 2.99627399604128973d-11) &
      *xs + 2.57747807106988589d-10)*xs - 2.09330568435488303d-9)  &
      *xs + 1.59501329936987818d-8)*xs  - 1.13717900285428895d-7) &
      *xs + 7.55292885309152956d-7)*xs  - 4.64980751480619431d-6) &
      *xs + 2.63830365675408129d-5)*xs  - 1.37089870978830576d-4) &
      *xs + 6.47686503728103400d-4)*xs  - 2.76060141343627983d-3) &
      *xs + 1.05306034687449505d-2)*xs  - 3.57191348753631956d-2) &
      *xs + 1.07774527938978692d-1)*xs  - 2.96997075145080963d-1
    ys = (ys*xs + 8.64664716763387311d-1)*xs + 7.42047691268006429d-1
    rinex = ys - log(dkl)
    goto 30
10  continue
    if (dkl .ge. 85.d0) go to 20
    xs = 14.5d0 / (dkl + 3.25d0) - 1.d0
    ys = ((((((((((((((( - 1.62806570868460749d-12 &
      *xs - 8.95400579318284288d-13)*xs - 4.08352702838151578d-12) &
      *xs - 1.45132988248537498d-11)*xs - 8.35086918940757852d-11) &
      *xs - 2.13638678953766289d-10)*xs - 1.10302431467069770d-9) &
      *xs - 3.67128915633455484d-9)*xs  - 1.66980544304104726d-8) &
      *xs - 6.11774386401295125d-8)*xs  - 2.70306163610271497d-7) &
      *xs - 1.05565006992891261d-6)*xs  - 4.72090467203711484d-6) &
      *xs - 1.95076375089955937d-5)*xs  - 9.16450482931221453d-5) &
      *xs - 4.05892130452128677d-4)*xs  - 2.14213055000334718d-3
    ys    = ((ys*xs - 1.06374875116569657d-2)*xs - &
            8.50699154984571871d-2)*xs + 9.23755307807784058d-1
    rinex = exp(-dkl) * ys / dkl
    goto 30

20  continue
    rinex = 0.d0

30  continue
    return
end subroutine acrm_s13aaf

!***********************************************************************
subroutine acrm_tav(teta, refra, tau)
    implicit none
    real(8),intent(in)::teta,refra
    real(8),intent(out)::tau
    real(8) :: eps,pi12,r2,rp,rm,atav,rktav,ds,b1,xxx, &
            b2,btav,tp1,tp2,tp3,tp4,tp5,tp,ts,pi
    pi = acos(-1.d0)

    data eps/.1d-6/
    pi12   = pi/2.d0
    r2     = refra**2
    rp     = r2 + 1.d0
    rm     = r2 - 1.d0
    atav   = (refra + 1.d0)**2/2.d0
    rktav  = -(r2 - 1.d0)**2/4.d0
    ds     = sin(teta)

    if (abs(teta) .le. eps) then
       tau = 4.d0*refra/(refra + 1.d0)**2
    else
       if (abs(teta - pi12) .le. eps) then
          b1  = 0.d0
       else
          xxx = (ds**2 - rp/2.d0)**2 + rktav
          b1  = dsqrt(xxx)
       endif

       b2   = ds**2 - rp/2.d0
       btav = b1 - b2
       ts   = (rktav**2/(6.d0*btav**3) + rktav/btav - btav/2.d0) - &
              (rktav**2/(6.d0*atav**3) + rktav/atav - atav/2.d0)
       tp1  = -2.d0*r2*(btav - atav)/rp**2
       tp2  = -2.d0*r2*rp*log(btav/atav)/rm**2
       tp3  = r2*(1.d0/btav - 1.d0/atav)/2.d0
       tp4  = 16.d0*r2**2*(r2**2 + 1.d0)*log((2.d0*rp*btav - rm**2)/ &
              (2.d0*rp*atav - rm**2))/(rp**3*rm**2)
       tp5  = 16.d0*r2**3*(1.d0/(2.d0*rp*btav - rm**2) - 1.d0/ &
              (2.d0*rp*atav - rm**2))/rp**3
       tp   = tp1 + tp2 + tp3 + tp4 + tp5
       tau  = (ts + tp)/(2.d0*ds**2)
    endif
    return
end subroutine acrm_tav

!*****************************************************************
subroutine gamma(th2, th1, phi, thm, ee, szz, rrl, ttl, rn, rk, &
     ellmy, ellsig, gg1, gg2, gamm)
    implicit none
    real(8),intent(in)::th2, th1, phi, thm, ee, szz, rrl, ttl, rn, rk, ellmy, ellsig
    real(8),intent(out)::gg1, gg2, gamm
    real(8) :: fgamm(5),eps3,eps5,eps,pi21,pi4,pi12,pi13,pi14,sth1, &
            cth1,cth2,calph,alph,salph,calp2,gmf,gl,y4,thp,gammf,gr0, &
            gt0,gr,gt,t11,t22,sg2,sg1,sgmr,sgmt,yg,thmc,st1,ct1, &
            st2,ct2,sthm,cthm,sp,cp,stt1,ctt1,e1,e2,s2,s3,ctg2,ctg1, &
            xg,pp,y1,x2,x1,fa,fb,sfa,sfb,cfa,cfb,gr1,gt1,grx,gtx, &
            gammd,szs,szv,ggx,sth2,pi
    integer ::jj,ii,i1,j
    data eps3/.01d0/, eps5/.1d-2/, eps/.1d-5/

    pi = acos(-1.d0)

    pi21  = pi/2.d0
    pi4   = pi*2.d0
    pi12  = 1.d0/pi4
    pi13  = pi12/1.5d0
    pi14  = pi12*4.d0
    sth1  = sin(th1)
    sth2  = sin(th2)
    cth1  = cos(th1)
    cth2  = cos(th2)
    calph = sth1*sth2*cos(phi) + cth1*cth2
    if(calph.gt.1.0) calph=1.0
    if(calph.lt.-1.0) calph=-1.0
    !
    alph  = acos(calph)
    salph = sin(alph)
    calp2 = cos(alph/2.d0)
    call gmfres(calp2, rn, rk, gmf)

    if (ee .le. eps3) then
       gl  = 1.d0
    else
       y4  = abs(cth2 + cth1)*.5d0/calp2
       !
       if(y4.gt.1.0) calph=1.0
       if(y4.lt.-1.0) calph=-1.0
       !
       thp = acos(y4)
       call gleaf(thm, ee, thp, gl)
    endif
    gammf = gmf*gl*.125d0

    gr0   = (salph + (pi - alph)*calph)*pi13
    gt0   = (salph - alph*calph)*pi13

    if (ee .lt. .4d0) then
       gr   = gr0
       gt   = gt0
       gg2  = .5d0
       gg1  = .5d0
       goto 99
    endif

    if (th1 .le. th2) then
       t11  = th1
       t22  = th2
       jj   = 1
    else
       t11  = th2
       t22  = th1
       jj   = 0
    endif

    sg2   = 0.d0
    sg1   = 0.d0
    sgmr  = 0.d0
    sgmt  = 0.d0
    yg    = 0.d0
    i1    = 0
    thmc  = pi21 - thm
    if (abs(thmc) .lt. eps) thmc = abs(thmc)
    st1   = sin(t11)
    ct1   = cos(t11)
    st2   = sin(t22)
    ct2   = cos(t22)
    sthm  = sin(thm)
    cthm  = cos(thm)
    sp    = sin(phi)
    cp    = cos(phi)
    stt1  = st1*st2
    ctt1  = ct1*ct2
    e1    = st1*ct2
    e2    = ct1*st2
    s2    = e1*cp + e2
    s3    = e1*sp
    ctg2  = 1.d30
    ctg1  = 1.d30
    if (st1 .ne. 0.d0) ctg2  = ct1/st1
    if (st2 .ne. 0.d0) ctg1  = ct2/st2

    if (thmc .lt. t22) goto 47

    xg   = sthm*sthm
    pp   = calph*xg + ctt1*(2.d0 - 3.d0*xg)
    pp   = pp*pi
    yg   = pp
    if (yg .gt. 0.d0) sgmr = sgmr + yg
    if (yg .lt. 0.d0) sgmt = sgmt - yg
    y1  = ct2*cthm
    sg1 = sg1 + abs(y1)

46  continue
    y1    = ct1*cthm
    sg2   = sg2 + abs(y1)
    goto 48
47  continue
    if (thmc .lt. t11) goto 50

    x2 = cthm/sthm
    xg = -ctg1*x2
    x1 = sqrt(1.d0 - xg*xg)
    fa = atan2(x1, xg)
    fb = pi4 - fa
    xg   = fb - fa
    if (xg .gt. eps5) then
       if ((pi4 - xg) .lt. eps5) then
          xg   = sthm*sthm
          pp   = calph*xg + ctt1*(2.d0 - 3.d0*xg)
          pp   = pp*pi
          goto 172
       endif

       sfa  = sin(fa)
       sfb  = sin(fb)
       cfa  = cos(fa)
       cfb  = cos(fb)
       pp   = xg*ctt1*cthm*cthm
       y1   = xg + sfb*cfb - sfa*cfa
       xg   = cfa - cfb
       y1   = y1*cp + sp*xg*(cfa + cfb)
       pp   = pp + stt1*.5d0*y1*sthm*sthm
       y1   = s2*(sfb - sfa) + s3*xg
       pp   = pp + y1*sthm*cthm
       goto 172
    endif
    pp   = 0.d0

172  continue
    yg  = pp
    if (yg .gt. 0.d0) sgmr = sgmr + yg
    if (yg .lt. 0.d0) sgmt = sgmt - yg
    xg   = sthm*sthm
    pp   = calph*xg + ctt1*(2.d0 - 3.d0*xg)
    pp   = pp*pi
    yg   = pp - yg
    if (yg .gt. 0.d0) sgmr = sgmr + yg
    if (yg .lt. 0.d0) sgmt = sgmt - yg

    x2   = ct2*cthm
    x1   = st2*sthm/x2
    x1   = sqrt(x1*x1 - 1.d0)
    xg   = atan2(1.d0, x1)
    xg   = (xg + x1)*x2
    yg   = xg*pi14
    sg1  = sg1 + abs(yg)
    goto 46

50  continue

    x2       = cthm/sthm
    xg       = -ctg1*x2
    x1       = sqrt(1.d0 - xg*xg)
    fa       = atan2(x1, xg)
    fgamm(2) = fa
    fgamm(3) = pi4 - fa
    xg       = -ctg2*x2
    x1       = sqrt(1.d0 - xg*xg)
    fa       = atan2(x1, xg)
    fb       = phi - fa
    if (fb .lt. 0.d0) fb = fb + pi4
    fgamm(4) = fb
    fgamm(5) = phi + fa

    do 75 ii = 2, 4
       i1 = ii + 1
       do 75 j = i1, 5
          fa = fgamm(ii)
          fb = fgamm(j)
          if (fb .gt. fa) goto 75
          fgamm(ii) = fb
          fgamm(j)  = fa
75  continue

    fgamm(1) = fgamm(5) - pi4
    i1       = 1
76  continue
    ii   = i1
    i1   = ii + 1
    fa   = fgamm(ii)
    fb   = fgamm(i1)
    xg   = fb - fa
    if (xg .gt. eps5) then
       if ((pi4 - xg) .lt. eps5) then
          xg   = sthm*sthm
          pp   = calph*xg + ctt1*(2.d0 - 3.d0*xg)
          pp   = pp*pi
          goto 74
       endif
       sfa  = sin(fa)
       sfb  = sin(fb)
       cfa  = cos(fa)
       cfb  = cos(fb)
       pp   = xg*ctt1*cthm*cthm
       y1   = xg + sfb*cfb - sfa*cfa
       xg   = cfa - cfb
       y1   = y1*cp + sp*xg*(cfa + cfb)
       pp   = pp + stt1*.5d0*y1*sthm*sthm
       y1   = s2*(sfb - sfa) + s3*xg
       pp   = pp + y1*sthm*cthm
       goto 74
    endif

    pp   = 0.d0

74  continue
    yg    = pp
    if (yg .gt. 0.d0) sgmr = sgmr + yg
    if (yg .lt. 0.d0) sgmt = sgmt - yg
    if (i1 .le. 4) goto 76

    x2   = ct1*cthm
    x1   = st1*sthm/x2
    x1   = sqrt(x1*x1 - 1.d0)
    xg   = atan2(1.d0, x1)
    xg   = (xg + x1)*x2
    yg   = xg*pi14
    sg2  = sg2 + abs(yg)

    x2   = ct2*cthm
    x1   = st2*sthm/x2
    x1   = sqrt(x1*x1 - 1.d0)
    xg   = atan2(1.d0, x1)
    xg   = (xg + x1)*x2
    yg   = xg*pi14
    sg1  = sg1 + abs(yg)

48  continue
    gr1 = sgmr*pi12
    gt1 = sgmt*pi12
    grx = gr0 - .0102d0 +(1.742d0*ellmy - .4557d0*ellsig)*(gr1 - gr0)
    gr  = max(grx, 0.d0)
    gtx = gt0 + 1.14d-4 +(.2693d0*ellmy + 5.821d0*ellsig)*(gt1 - gt0)
    gt  = max(gtx, 0.d0)
    gg2 =max((2.653d0*ellmy+1.432d0*ellsig)*(sg2-.5d0)+.50072d0, 0.d0)
    gg1 =max((2.653d0*ellmy+1.432d0*ellsig)*(sg1-.5d0)+.50072d0, 0.d0)
    if (jj .eq. 1) then
       ggx  = gg1
       gg1  = gg2
       gg2  = ggx
    endif

99  continue

    gammd = gr*rrl + gt*ttl
    szs = (szz - 1.d0)*(1.d0 - cth1) + 1.d0
    szv = (szz - 1.d0)*(1.d0 - cth2) + 1.d0
    gg1  = gg1*szs
    gg2  = gg2*szv
    gamm = gammd + gammf
    return
end subroutine gamma

!*****************************************************************
subroutine gmfres (calp2, rn, pkhair, gmf)
    implicit none
    real(8),intent(in)::calp2,rn,pkhair
    real(8),intent(out)::gmf
    real(8) :: pi12,x2,ag,bg,xy,cg,sa2,yg,yy,pi

    pi = acos(-1.d0)

    pi12 = pi/2.d0
    x2  = calp2*calp2
    ag  = x2*2.d0 - 1.d0 + rn*rn
    bg  = 1.d0 + (ag - 2.d0)*x2
    xy  = ag - x2
    cg  = 2.d0*calp2*sqrt(xy)
    sa2 = 1.d0 - x2
    yg  = (bg + sa2*cg)*(ag + cg)
    yg  = (ag - cg)*bg/yg
    yy  = sqrt(sa2)/pi12/calp2*pkhair
    gmf = exp(-yy)*yg
    return
end subroutine gmfres

!*****************************************************************
subroutine gleaf (thm, ee_0, thl, gl)
    implicit none
    real(8),intent(in)::thm,ee_0,thl
    real(8),intent(out)::gl
    real(8) :: bb,es,tms,eps,sthm,cthm,u1,u2,u3,u4,xg,x1,x2,pi,ee
    save bb, es, tms
    data bb/1.d0/, es/0.d0/, tms/0.d0/, eps/.1d0/,sthm/0.d0/, cthm/1.d0/

    ee = ee_0

    pi = acos(-1.d0)

    if (ee .lt. eps) then
       gl = 1.d0
       return
    endif

    if (ee .eq. 1.d0) ee = .999999d0
    if (ee .ne. es .or. thm .ne. tms) then
       sthm   = sin(thm)
       cthm   = cos(thm)
       u1     = ee*cthm
       u3     = ee*sthm
       u2     = sqrt(1.d0 - u1*u1)
       u4     = sqrt(1.d0 - u3*u3)
       xg     = log((u4 + u1)/(u2 - u3))
       x1     = atan2(u3, u4) - atan2(u1, u2)
       x2     = sthm*xg - cthm*x1
       bb     = ee/x2
       es     = ee
       tms    = thm
    endif
    gl = bb/sqrt(1.d0 - (ee*cos(thm - thl))**2)
    return
end subroutine gleaf

!*****************************************************************
subroutine dif2(thetv, thets, &
    gg1s, gg1v, ul1, rrl1, ttl1, ellmy1, ellsi1, thm1, &
    gg2s, gg2v, ul2, rrl2, ttl2, ellmy2, ellsi2, thm2, &
    rsl, thss, bdplants, bdsoil)

    implicit none
    real(8),intent(in)::thetv,thets,gg1s,gg1v,ul1,rrl1,ttl1,ellmy1,ellsi1, &
     thm1,gg2s,gg2v,ul2,rrl2,ttl2,ellmy2,ellsi2,thm2,rsl,thss
    real(8),intent(out)::bdplants,bdsoil
    real(8) :: rdd1,rdo1,rso1,tdd1,tdo1,tsd1,ggj1,rdd2,rdo2, &
     rso2,tdd2,tdo2,tsd2,ggj2,sq,ts11,ts12,ts21,ts22,ts1,ts2,fac, &
     rdd,rso,rdo,tdd,tsd,tdo,bcs,bcd,phi,rsosoil,rsdsoil,rodsoil, &
     rddsoil,rsd1,rsd2,skyl

    data phi/0.d0/
    call acrm_soil(rsl,thetv,thets,thss,phi,rsosoil,rsdsoil,rodsoil,rddsoil)

    skyl = 0.d0

    if (ul1 .le. 0d0) then
       rdd1  = 0.d0
       rdo1  = 0.d0
       rsd1  = 0.d0
       rso1  = 0.d0
       tdd1  = 1.d0
       tdo1  = 0.d0
       tsd1  = 0.d0
    else
       ggj1 = (1.289d0*ellmy1 - 1.816d0*ellsi1)* &
                ((cos(thm1))**2  - .33333333333d0) + .31823d0
       call layer(thets, thetv, gg1s, gg1v, ggj1, ul1, rrl1, ttl1, &
     rdd1, tdd1, rdo1, tdo1, rsd1, tsd1, rso1)
    endif

    if (ul2 .le. 0d0) then
       rdd2  = 0.d0
       rdo2  = 0.d0
       rsd2  = 0.d0
       rso2  = 0.d0
       tdd2  = 1.d0
       tdo2  = 0.d0
       tsd2  = 0.d0
    else
       ggj2 = (1.289d0*ellmy2 - 1.816d0*ellsi2)* &
               ((cos(thm2))**2  - .33333333333d0) + .31823d0
       call layer(thets, thetv, gg2s, gg2v, ggj2, ul2, rrl2, ttl2, &
     rdd2, tdd2, rdo2, tdo2, rsd2, tsd2, rso2)
    endif

    sq   = 1.d0 - skyl
    ts11 = exp(-ul1*gg1s/cos(thets))
    ts12 = exp(-ul1*gg1v/cos(thetv))
    ts21 = exp(-ul2*gg2s/cos(thets))
    ts22 = exp(-ul2*gg2v/cos(thetv))
    ts1  = ts11*ts21
    ts2  = ts12*ts22

    fac  = 1.d0/(1.d0 - rdd1*rdd2)
    rdd  = rdd2 + tdd2*rdd1*tdd2*fac
    rso  = rso2 + ts21*rso1*ts22 + (ts21*rsd1*tdo2 + tsd2*rdd1*tdo2 &
           + tsd2*rdo1*ts22)*fac
    rdo  = rdo2 + (tdd2*rdo1*ts22 + tdd2*rdd1*tdo2)*fac
    tdd  = tdd1*tdd2*fac
    tsd  = (tsd1*ts21 + tsd2*tdd1)*fac
    tdo  = (tdd1*tdo2 + tdo1*ts22)*fac

    fac  = 1.d0/(1.d0 - rdd*rddsoil)
    bcs  = sq*(rso + (ts1*rsdsoil + tsd*rddsoil)*fac*tdo)
    bcd  = skyl*(rdo + tdd*rddsoil*tdo*fac)
    bdplants = bcs + bcd

    bdsoil   = ((sq*tsd + skyl*tdd)*rodsoil + &
               sq*ts1*rsdsoil*rdd*rodsoil)*ts2*fac

    return
end subroutine dif2

!*********************************************************************
subroutine layer(thets, thetv, ggs, ggv, ggj, ul, rrl, ttl, &
     rdd, tdd, rdo, tdo, rsd, tsd, rso)
    implicit none
    real(8),intent(in)::thets,thetv,ggs,ggv,ggj,ul,rrl,ttl
    real(8),intent(out)::rdd,tdd,rdo,tdo,rsd,tsd,rso
    real(8) :: eps,rtp,rtm,bf,vks,vkv,vsig,vatt,vssf,vssb,vmm2, &
            vmm,vvv,vuu,vh1,vh2,vcc,vdd,exku1,exku2,xxx,yy1,yy2,yy3, &
            xx3,exmu1,exmu2,delt,delta,deltb
    real(8),external::eint
    data eps/.1d-5/

    rtp   = (rrl + ttl)/2.d0
    rtm   = (rrl - ttl)/2.d0
    bf    = rtm*ggj

    vks   = ggs/cos(thets)
    vkv   = ggv/cos(thetv)
    vsig  = rtp + bf
    vatt  = 1.d0 - rtp + bf
    vssf  = vks*rtp - bf
    vssb  = vks*rtp + bf
    vmm2  = vatt**2 - vsig**2
    vmm   = sqrt(vmm2)
    vvv   = vkv*rtp + bf
    vuu   = vkv*rtp - bf
    vh1   = (vatt + vmm)/vsig
    vh2   = 1.d0/vh1
    vcc   = (vssf*vsig - vssb*(vks - vatt))/(vmm2 - vks**2)
    vdd   = (vssb*vsig + vssf*(vks + vatt))/(vmm2 - vks**2)

    exku1 = exp(-vks*ul)
    if (vmm .lt. eps) then

      exku2 = exp(-vkv*ul)
      xxx   = (2.d0 + ul*(1.d0 + vmm2))
      yy1   = eint((vkv - vmm)*ul)
      yy2   = eint((vkv + vmm)*ul)
      yy3   = eint(vkv*ul)
      xx3   = (1.d0 - (1.d0 + vkv*ul)*exku2)/(vkv*vkv - vmm2)
      rdd   = ul*(1.d0 - vmm2)/xxx
      tdd   = 2.d0/xxx
      tsd   = vdd*(exku1 - tdd) - (1d0 - vmm2)*ul*vcc*exku1/xxx

      rsd   = vcc - (2.d0*vcc*exku1 + vdd*ul*(1.d0 - vmm2))/xxx
      tdo   = (((vuu*(1.d0 + vmm2) + vvv*(1.d0 - vmm2))*xx3) + &
              ul*vuu*(yy1 + yy2))/xxx

      rso   = (((1.d0 + vkv*ul)*exku2 - 1.d0)*(vvv+vuu)* &
              (vcc*exku2 - vdd) - &
              ul*vkv*vkv*(2.d0*vuu*vcc*exku2 + (vuu + vvv)*ul*vdd + &
              2.d0*vvv*vdd)*yy3)/(2.d0 + ul)/vkv/vkv + &
              ul*(vvv*vdd + vuu*vcc)*eint((vkv + vks)*ul)

      rdo   = ((vvv + vuu)*((1.d0 + vkv*ul)*exku2 - 1.d0) + &
              ul*vkv*vkv*(vvv*(2.d0 + ul) + vuu*ul)*yy3)/ &
              (2.d0 + ul)/vkv/vkv
    else
      exmu1 = exp(vmm*ul)
      exmu2 = 1.d0/exmu1
      delt  = vh1*exmu1 - vh2*exmu2
      rdd   = (exmu1 - exmu2)/delt
      tdd   = (vh1 - vh2)/delt
      delta = vh2*vcc*exku1 - vdd*exmu1
      deltb = vdd*exmu2 - vh1*vcc*exku1
      tsd   = (vcc*(exku1*(exmu2 - exmu1)) + &
              vdd*(vh2 - vh1))/delt + vdd*exku1
      rsd   = (delta + deltb)/delt + vcc
      tdo   = ul*((vuu*vh1 + vvv)*eint((vkv - vmm)*ul) - &
              (vuu*vh2 + vvv)*eint((vkv + vmm)*ul))/delt
      rso   = ul*((vvv*vdd + vuu*vcc)*eint((vkv + vks)*ul) + &
              ((vvv*vh1 + vuu)*delta*eint((vkv + vmm)*ul) + &
              (vvv*vh2 + vuu)*deltb*eint((vkv - vmm)*ul))/delt)

      rdo   = ul*(exmu1*(vvv*vh1 + vuu)*eint((vkv + vmm)*ul) - &
              exmu2*(vvv*vh2 + vuu)*eint((vkv - vmm)*ul))/delt
    endif

    return
end subroutine layer

!*********************************************************************
subroutine liberty(rnnl, coeff, vai, asp, rinf, refl, tran)
    use acrm_paras,only:thknot,p14,nknots
    implicit none
    real(8),intent(in)::rnnl,coeff,vai,asp
    real(8),intent(out)::rinf,refl,tran
    real(8) :: rnn0,sme,alpha,calp,salp,rfr,sacrit,smint,smi,rmm,rtt, &
        rxx,aaa,bbb,ccc,diskr,rrr,rb,tb,frac,yyy,top,bot1,bot2,tinf, &
        curt,curr,prevt,prevr,acritic,dr
    integer ::i1,ja,icritic,js
    dr = acos(-1.d0) / 180.0

    data rnn0/1.d0/
    sme = 0.0
    do i1 = 2, nknots
       alpha = thknot(i1)
       calp  = cos(alpha)
       salp  = sin(alpha)
       call acrm_fresnel(rnnl, calp, rfr)
       sme  = sme + rfr*salp*p14(i1)
    enddo
    sme = sme*2.d0

    sacrit  = rnn0/rnnl
    !
    if(sacrit.gt. 1.0)sacrit=1.0
    if(sacrit.lt. -1.0)sacrit=-1.0
    !
    acritic = asin(sacrit)
    icritic = int(acritic/dr)
    smint   = 0.d0
    do ja = 1, icritic
       alpha = dble(ja)*dr
       calp  = cos(alpha)
       salp  = sin(alpha)
       call acrm_fresnel(rnnl, calp, rfr)
       smint = smint + rfr*salp*calp
    enddo
    smint = smint*dr
    smi   = 1.d0 - sacrit**2 + 2.d0*smint

    rmm = 2.d0/coeff**2*(1.d0 - (coeff + 1.d0)*exp(-coeff))
    rtt = (1.d0 - smi)*rmm/(1.d0 - smi*rmm)
    rxx = asp/(1.d0 - (1.d0 - 2.d0*asp)*rtt)

    aaa = sme*rtt + rxx*rtt - sme - rtt - rxx*sme*rtt
    bbb = 1.d0 + rxx*sme*rtt - 2.d0*rtt*(rxx*sme)**2
    ccc = 2.d0*sme*rxx**2*rtt - rxx*rtt - 2.d0*rxx*sme

    diskr = bbb**2 - 4.d0*aaa*ccc
    if (diskr .ge. 0.d0) then
       rrr = (sqrt(diskr) - bbb)/(2.d0*aaa)
       if ((rrr .lt. 0.d0) .or. (rrr .gt. 1.d0)) then
          rrr = (-sqrt(diskr) - bbb)/(2.d0*aaa)
       endif
     else
       rinf = -99.d0
       refl = rinf
       tran = rinf
       return
     endif

    aaa = 2.d0*rxx*sme + rxx*rtt - 2.d0*rtt*sme*rxx**2
    rb  = aaa
    tb  = sqrt((rrr - rb)*(1.d0 - rrr*rb)/rrr)

    frac = vai - int(vai)
    yyy  = (1.d0 + tb)**2 - tb**2
    top  = tb**(1.d0 + frac)*yyy**(1.d0 - frac)
    bot1 = (1.d0 + tb)**(2.d0*(1.d0 - frac)) - rb**2
    bot2 = 1.d0 + 64.d0/3.d0*frac*(frac - .5d0)*(frac - 1.d0)*.001d0
    tinf = top/bot1/bot2
    rinf = (1.d0 + rb**2 - tb**2 - sqrt((1.d0 + rb**2 - tb**2)**2 - &
     4.d0*rb**2*(1.d0 - tinf**2)))/rb*.5d0

    curt = 1.d0
    curr = 0.d0
    if (vai .ge. 2.d0) then
       do js = 1, int(vai - 1.d0)
          prevt = curt
          prevr = curr
          curt = prevt*tb/(1.d0 - prevr*rb)
          curr = prevr + prevt**2*rb/(1.d0 - prevr*rb)
       enddo
    endif

    tran  = curt*tinf/(1.d0 - rinf*curr)
    refl  = curr + curt**2*rinf/(1.d0 - rinf*curr)
    return
end subroutine liberty

!*********************************************************************
subroutine acrm_fresnel(rnnl, calp, rfr)
    implicit none
    real(8),intent(in)::rnnl,calp
    real(8),intent(out)::rfr
    real(8) :: sa2,aag
    sa2 = 1.d0 - calp**2
    aag = sqrt(rnnl**2 - sa2)
    rfr = (((aag - calp)/(aag + calp))**2 + &
        ((rnnl**2*calp - aag)/(rnnl**2*calp + aag))**2)/2.d0
    return
end subroutine acrm_fresnel
